import React, { Fragment, useEffect } from 'react';
import * as d3 from 'd3';
import axios from 'axios';

function getSmartTicks(val) {
  //base step between nearby two ticks
  let step = Math.pow(10, val.toString().length - 1);
  //modify steps either: 1, 2, 5, 10, 20, 50, 100, 200, 500, 1000, 2000...
  if (val / step < 2) {
      step = step / 5;
  } else if (val / step < 5) {
      step = step / 2;
  }
  //add one more step if the last tick value is the same as the max value
  //if you don't want to add, remove "+1"
  const slicesCount = Math.ceil((val + 1) / step);

  return {
    endPoint: slicesCount * step,
    count: Math.min(10, slicesCount) //show max 10 ticks
  };
};

const Heatmap = () => {
  useEffect(() => {
    const url = 'http://localhost:3010';
    axios.get(url).then(res => {
      console.log('fetching data...');
      // TODO have a separate table for data values such as
      // hzLow, hzHigh and link it with freqs id
      if (res.data) {
        // TODO tidy up
        const data = res.data;
        const baseHz = res.data[0].hzLow;
        const topHz = res.data[0].hzHigh;
        const baseTemperature = res.data[0].hzLow;
        const step = (topHz - baseHz) / res.data[0].freqs.length;
        let hz = baseHz;
        const dataVis = [];

        const mappedData = data.map((el, i) => {
          console.log('started');
          hz = baseHz;
          return el.freqs.map((db) => {
            const tmp = {};
            tmp.createdAt = el.createdAt;
            tmp.db = db;
            tmp.hz = hz;
            hz = Math.round(hz + step);
            dataVis.push(tmp);
            return tmp;
          });
        });

        console.log('top: ', topHz, '\nbase: ', baseHz);

        const hzRange = [baseHz, topHz]; 
        const margin = {top: 100, right: 50, bottom: 100, left: 50};
        const width = 9450 - margin.left - margin.right;
        const height = 950 - margin.top - margin.bottom;

        const svg = d3.select('.chart')
          .attr('width', width + margin.left + margin.right)
          .attr('height', height + margin.top + margin.bottom)
          .append('g')
          .attr('transform', 'translate(' + margin.left + ',' + margin.top + ')');

        const xScale = d3.scaleBand()
          .range([0, width])
          .domain(mappedData[0].map(d => {
            return d.hz;
          }))
          .padding(0.01);

        svg.append('g')
          .attr('transform', 'translate(0,' + height + ')')
          .call(d3.axisBottom(xScale)
            .ticks(30, 's'))
          .attr('class','xAxis');


        const yScale = d3.scaleBand()
          .range([ height, 0 ])
          .domain(mappedData.map((el) => el.map((item) => item.createdAt)
            .filter((v, i, a) => a.indexOf(v) === i)[0]))
          .padding(0.01);

        svg.append("g")
          .call(d3.axisLeft(yScale))
          .attr('class','yAxis');

        svg.append('text')
          .attr('transform','translate(' + (width / 2) + ',' + (height + 40) + ')')
          .style('text-anchor','middle')
          .text('HZ');

        const colorScale = d3.scaleLinear()
          .range(['blue', 'yellow', 'red'])
          .domain([-127, 0, 127]);

        svg.selectAll('g.x text')
          .attr('transform', 'translate(-10,10) rotate(315)');

        svg.selectAll()
            .data(dataVis)
            .enter()
            .append("rect")
            .attr("x", function(d) { 
              return xScale(d.hz) 
            })
            .attr("y", function(d) { 
              return yScale(d.createdAt) 
            })
            .attr("width", xScale.bandwidth() )
            .attr("height", yScale.bandwidth() )
            .style("fill", function(d) { return colorScale(d.db)} )

        svg.selectAll('g.x')
          .attr('transform', 'translate(-10,10) rotate(315)');
      }
    });
  }, ['timeDataTaken']);

  return (
    <Fragment>
      <h1>Heatmap</h1>
      <svg className='chart'></svg>
    </Fragment>
  );
};

const App = () => {
  return (
    <div style={{height: '100%'}} className="App">
      <Heatmap />
    </div>
  );
};

export default App;
