import React, { Fragment, useEffect, useState } from 'react';
import * as d3 from 'd3';
import axios from 'axios';

const url = 'http://localhost:3010';
const reader = new FileReader();
// D3 stuff 
const margin = {top: 100, right: 50, bottom: 100, left: 50};
const width = 9450 - margin.left - margin.right;
const height = 1250 - margin.top - margin.bottom;
let x, y;

const svg = d3.select('.chart')
  .attr('width', width + margin.left + margin.right)
  .attr('height', height + margin.top + margin.bottom)
  .append('g')
  .attr('transform', 'translate(' + margin.left + ',' + margin.top + ')');

const Heatmap = () => {
  // TODO merge/review containers...
  const [samples, setSamples] = useState([]);
  const [d3data, setD3Data] = useState([]);
  const [decodedFreqs, setDecodedFreqs] = useState();

  // Testing
  const [xScale, setX] = useState();
  const [yScale, setY] = useState();

  // Blob to string

  const freqsMapper = (data, hzLow, hzHigh, createdAt) => {
    const step = (hzHigh - hzLow) / data.freqs.length;
    let hz = hzLow;
    const tmpArray = [];

    return data.freqs.map((db) => {
      const tmpObj = {};
      tmpObj.createdAt = createdAt;
      tmpObj.db = db;      
      tmpObj.hz = hz;
      hz = Math.round(hz + step);
      tmpArray.push(tmpObj);
      return tmpObj;
    });
  };

  // FileReader 'onload' event
  reader.onload = ((s) => {
    return (e) => {
      console.log('s: ', s)
      const data = JSON.parse(e.target.result);

      if (data) {
        const { hzLow, hzHigh, createdAt } = data
        const newLine = freqsMapper(data, hzLow, hzHigh, createdAt);
        let tmpArr = [...s];
        tmpArr.unshift(newLine);
        tmpArr.pop();
        setSamples(tmpArr);
      }
    }
  })(samples)

  // Pull data from the server and store it in the state
  const getData = async () => {
    const res = await axios.get(url);
    console.log('server response: ', res.data);
    const data = res.data;
    const baseHz = res.data[0].hzLow;
    const topHz = res.data[0].hzHigh;
    const step = (topHz - baseHz) / res.data[0].freqs.length;
    let hz = baseHz;
    const dataVis = [];

    // TODO setSamples(map...)
    const mappedData = data.map((el, i) => {
      hz = baseHz;
      return el.freqs.map((db) => {
        const tmp = {};
        tmp.createdAt = el.createdAt;
        tmp.db = db;
        tmp.hz = hz;
        hz = Math.round(hz + step);
        dataVis.push(tmp);
        return tmp;
      });
    });

    // update state
    setD3Data([...dataVis])
    setSamples([...mappedData]);

    await new Promise((r) => setTimeout(r, 3000));

    return new Promise((resolve) => {
      if (mappedData) return resolve(mappedData);
      resolve('mappedData -> undefined')
    });
  };

  useEffect(() => {
    const wsClient = new WebSocket('ws://localhost:8080');

    wsClient.onopen = () => onConnection(); 
    wsClient.onmessage = (msg) => handleMessage(msg);
    wsClient.onclose = (e) => console.log('closing ws connection... ', e);

    const hooks = {
      setX: () => setX(),
      setY: () => setY(),
    };

    console.log('hooks: ', hooks)
    console.log('setX(): ', setX)

    getData().then((data) => {
      // X d3 scale
      x = d3.scaleBand()
        .range([0, width])
        .domain(data[0].map(d => {
          return d.hz;
        }))
        .padding(0.01);

      // Y d3 scale
      y = d3.scaleBand()
        .range([ height, 0 ])
        .domain(data.map((el) => el.map((item) => item.createdAt)
          .filter((v, i, a) => a.indexOf(v) === i)[0]))
        .padding(0.01);

      svg.append('g')
        .attr('transform', 'translate(0,' + height + ')')
        .call(d3.axisBottom(x)
          .ticks(30, 's'))
        .attr('class','xAxis');

      svg.append("g")
        .call(d3.axisLeft(y))
        .attr('class','yAxis');
    });

    return () => {
      console.log('unmounting...');
    }
  }, []);

  // websocket event handlers
  // ===================================================================
  const handleMessage = (msg) => {
    console.log('msg received: ', msg.data);
    reader.readAsText(msg.data);
  };

  const onConnection = () => {
    console.log('websocket: connected');
  };

  console.log('re-rendering: ', samples.length)

  console.log('x scale: ', xScale)
  if (samples.length > 0 && xScale && yScale) {
    console.log('should print THE map')
    // d3 stuff
    /*
    const svg = d3.select('.chart')
      .attr('width', width + margin.left + margin.right)
      .attr('height', height + margin.top + margin.bottom)
      .append('g')
      .attr('transform', 'translate(' + margin.left + ',' + margin.top + ')');
    */

    /* not needed for now...
    svg.append('text')
      .attr('transform','translate(' + (width / 2) + ',' + (height + 40) + ')')
      .style('text-anchor','middle')
      .text('HZ');
    */

    const colorScale = d3.scaleLinear()
      .range(['blue', 'yellow', 'red'])
      .domain([-40, 0, 40]);

    svg.selectAll('g.x text')
      .attr('transform', 'translate(-10,10) rotate(315)');

    svg.selectAll()
        .data(d3data)
        .enter()
        .append("rect")
        .attr("x", function(d) {
          return xScale(d.hz)
        })
        .attr("y", function(d) {
          return yScale(d.createdAt)
        })
        .attr("width", xScale.bandwidth() )
        .attr("height", yScale.bandwidth() )
        .style("fill", function(d) { return colorScale(d.db)} )
  };

  // Testing... to remove
  const renderHeatmap = () => {
  };

  return (
    <Fragment>
      {renderHeatmap()}
    </Fragment>
  );
};

const Main = () => {
  const [close, setClose] = useState(false);

  const handleClose = (e) => {
    setClose(close ? false : true);
    console.log('closing connection');
  };

  return (
    <Fragment>
      <button onClick={handleClose}>close</button>
      <svg className='chart'></svg>
      {close ? null : <Heatmap />} 
    </Fragment>
  );
}

const App = () => {
  return (
    <div style={{height: '100%'}} className="App">
      <Main />
    </div>
  );
};

export default App;
